let sel=(a,b)=>{if(!b){b=document}
return b.querySelector(a)}
let all=(a,b)=>{if(!b){b=document}
return b.querySelectorAll(a)}
let addEvt=(a,b,c)=>{if(typeof a==='string'){a=all(a);for(e of a){e.addEventListener(b,c)}}else if(Object.prototype.toString.call(a)==='[object NodeList]'){for(e of a){e.addEventListener(b,c)}}else if(Array.isArray(a)){for(e of a){for(el of all(e)){el.addEventListener(b,c)}}}else{a.addEventListener(b,c)}};let loaded=(a)=>{addEvt(document,'DOMContentLoaded',a)}