const sel=(a,b)=>{if(!b){b=document}
if(b.querySelector(a)){return b.querySelector(a)}else{console.log(`i cant find ${a}`)}}
const all=(a,b)=>{if(!b){b=document}
if(b.querySelectorAll(a).length===0){console.log(`i cant find ${a}`)}else{return b.querySelectorAll(a)}}
const addEvt=(a,b,c)=>{if(typeof a==='string'){a=all(a)
a.forEach((e)=>{e.addEventListener(b,c)})}else if(Object.prototype.toString.call(a)==='[object NodeList]'){a.forEach((e)=>{e.addEventListener(b,c)})}else if(Array.isArray(a)){a.forEach((e)=>{all(e).forEach((el)=>{el.addEventListener(b,c)})})}else{a.addEventListener(b,c)}}
const loaded=(a)=>{addEvt(document,'DOMContentLoaded',a)}